    private void btnVenderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVenderActionPerformed
        String nomeProduto = (String) comboBoxProdutosAVenda.getSelectedItem();

        if (nomeProduto != null && !nomeProduto.trim().isEmpty()) {
            try {
                ProdutosDAO produtosdao = new ProdutosDAO();
                ProdutosDTO produto = produtosdao.getProdutoPorNome(nomeProduto);

                if (produto != null) {
                    produtosdao.venderProduto(produto.getId());
                    listarProdutos(); // Atualizar a listagem de produtos
                    preencherProdutosAVenda(); // Atualizar a lista de produtos a venda
                } else {
                    JOptionPane.showMessageDialog(null, "Produto n√£o encontrado.");
                }
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Erro ao vender o produto: " + e.getMessage());
            }
        } else {
            JOptionPane.showMessageDialog(null, "Por favor, selecione um produto.");
        }
    }//GEN-LAST:event_btnVenderActionPerformed

